mapper.select:
SELECT E.ENTP_UNQ , E.ENTP_TP , E.ENTP_NM , E.SVC_TP , E.STT_MONTH_CNT , E.STT_DAY_CNT , E.REG_DTM , (SELECT DISTINCT 'true' FROM TBL_ENTP_ATCH_LST F WHERE F.ENTP_UNQ = E.ENTP_UNQ ) AS ATCH_FILE_BOOL FROM STT_ENTP_LST E WHERE 1=1
AND E.SVC_TP = #{svc_tp}
AND E.ENTP_TP = #{entp_tp}
AND E.ENTP_NM LIKE CONCAT('%', #{entp_nm}, '%')

mapper.update:
UPDATE STT_ENTP_LST
SET
     ENTP_TP       = #{entp_tp}
    ,ENTP_NM       = #{entp_nm}
    ,SVC_TP        = #{svc_tp}
    ,STT_MONTH_CNT = #{stt_month_cnt}
    ,STT_DAY_CNT   = #{stt_day_cnt}
    ,GNR_MEMO      = #{gnr_memo}
    ,CHG_USR_ID    = #{chg_usr_id}
    ,CHG_DTM       = now()
WHERE
    ENTP_UNQ = #{entp_unq}

UPDATE STT_CUST_LST
SET
     MEMB_DEPT_NM = #{memb_dept_nm}
    ,MEMB_NM      = #{memb_nm}
    ,MEMB_PST_NM  = #{memb_pst_nm}
    ,MEMB_TEL     = #{memb_tel}
    ,MEMB_EMAIL   = #{memb_email}
    ,CHG_USR_ID   = #{chg_usr_id}
    ,CHG_DTM      = now()
WHERE
    CUST_UNQ = #{cust_unq}

UPDATE STT_ENTP_SVR_LST
SET
     SVR_HST       = #{svr_hst}
    ,SVR_IP        = #{svr_ip}
    ,SVR_CONT      = #{svr_cont}
    ,USE_FLAG      = #{use_flag}
    ,RESC_USE_FLAG = #{resc_use_flag}
    ,TRN_USE_FLAG  = #{trn_use_flag}
    ,OS_VERS       = #{os_vers}
    ,KERN_VERS     = #{kern_vers}
    ,CPU_CNT       = #{cpu_cnt}
    ,TOTAL_MEM_SZ  = #{total_mem_sz}
    ,USED_MEM_SZ   = #{used_mem_sz}
    ,GPU_MODEL     = #{gpu_model}
    ,TOTAL_DISK_SZ = #{total_disk_sz}
    ,USED_DISK_SZ  = #{used_disk_sz}
    ,BASE_PATH     = #{base_path}
    ,LOG_PATH      = #{log_path}
    ,GNR_MEMO      = #{gnr_memo}
    ,REG_USR_ID    = #{reg_usr_id}
    ,REG_DTM       = #{reg_dtm}
WHERE
    ENTP_UNQ = #{entp_unq}
