mapper.select:
SELECT T.TASK_UNQ ,T.ENTP_UNQ ,T.CUST_UNQ ,T.TASK_TP ,T.TASK_JOB_TP ,T.TASK_USR_NM ,T.TASK_ST_DT ,T.TASK_ED_DT ,(SELECT DISTINCT 'true' FROM TBL_TASK_ATCH_LST F WHERE F.TASK_UNQ = T.TASK_UNQ ) AS ATCH_FILE_BOOL ,E.ENTP_NM FROM STT_ENTP_TASK_LST T LEFT JOIN STT_ENTP_LST E ON T.ENTP_UNQ = E.ENTP_UNQ WHERE 1=1 AND T.DEL_YN ='N'
AND E.ENTP_NM LIKE CONCAT('%', #{entp_nm}, '%')
AND T.TASK_TP = #{task_tp}
AND T.TASK_ST_DT = #{task_st_dt}
ORDER BY T.TASK_ST_DT DESC
LIMIT #{limit_num}

SELECT * FROM TBL_TASK_ATCH_LST WHERE 1=1 AND DEL_YN = 'N'
AND TASK_UNQ = #{task_unq}
AND ATCH_FILE_UNQ = #{atch_file_unq}

mapper.update:
UPDATE
    STT_ENTP_TASK_LST
SET
    TASK_TP             = #{task_tp}
  ,CUST_UNQ            = #{cust_unq}
  ,TASK_USR_UNQ        = #{task_usr_unq}
  ,TASK_USR_NM         = #{task_usr_nm}
  ,TASK_ST_DT          = #{task_st_dt}
  ,TASK_ST_TM          = #{task_st_tm}
  ,TASK_ED_DT          = #{task_ed_dt}
  ,TASK_ED_TM          = #{task_ed_tm}
  ,TASK_JOB_TP         = #{task_job_tp}
  ,SVC_EFC             = #{svc_efc}
  ,TASK_MEMO           = #{task_memo}
  ,STT_MONTH_TOTAL_CNT = #{stt_month_total_cnt}
  ,STT_MONTH_S_CNT     = #{stt_month_s_cnt}
  ,STT_MONTH_F_CNT     = #{stt_month_f_cnt}
  ,STT_DAY_TOTAL_CNT   = #{stt_day_total_cnt}
  ,STT_DAY_S_CNT       = #{stt_day_s_cnt}
  ,STT_DAY_F_CNT       = #{stt_day_f_cnt}
  ,CHG_USR_ID          = #{chg_usr_id}
  ,CHG_DTM             = now()
WHERE
    TASK_UNQ = #{task_unq}
